import axios from "axios";
import {
  AsyncThunk,
  createAsyncThunk,
  createSlice,
  Slice,
} from "@reduxjs/toolkit";

export const getList = createAsyncThunk("getList", async () => {
  const response = await axios.get("http://localhost:8000/list");
  return response.data;
});

export const addList = createAsyncThunk("addList", async (newList) => {
  const response = await axios.post("http://localhost:8000/list", newList);
  return response.data;
});

export type TodoState = {
  loading: "idle" | "pending";
  error: undefined | unknown;
  list: [];
};

const initialState: TodoState = {
  loading: "idle",
  error: undefined,
  list: [],
};

export const todoReducer = createSlice({
  name: "todoList",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder.addCase(getList.pending, (state) => {
      state.loading = "pending";
    });
    builder.addCase(getList.fulfilled, (state, { payload }) => {
      state.loading = "idle";
      state.error = undefined;
      state.list = payload;
    });
    builder.addCase(getList.rejected, (state, { payload }) => {
      state.loading = "idle";
      state.error = payload;
    });
  },
});

export default todoReducer.reducer;
