import axios from "axios";
import {
  AsyncThunk,
  createAsyncThunk,
  createSlice,
  Slice,
} from "@reduxjs/toolkit";

export const getList = createAsyncThunk("GET_TODO", async () => {
  const response = await axios.get("http://localhost:8000/list");
  return response.data;
});

export const addList = createAsyncThunk("ADD_TODO", async (newList) => {
  const response = await axios.post("http://localhost:8000/list", newList);
  return response.data;
});

// type todoReducerType {
//   name: string;
//   initialState: any;
//   reducers: any;
//   extraReducers: any;
// }
export type TodoState = {
  loading: boolean;
  error: undefined;
  list: Array;
};

const initialState: TodoState = {
  loading: true,
  error: undefined,
  list: [],
};

export const todoReducer = createSlice({
  name: "todoList",
  initialState,
  reducers: {},
  extraReducers: {
    // [getList.fulfilled]: (state, { payload }) => [...payload],
  },
});

export default todoReducer.reducer;
