import axios from "axios";

import {
  // AsyncThunk,
  createAction,
  createAsyncThunk,
  createSlice,
  // Slice,
} from "@reduxjs/toolkit";

export const minusCount = createAction("minusCount", function prepare(payload) {
  return payload;
});

export const plusCount = createAction("plusCount", function prepare(payload) {
  return payload;
});

export type TodoState = {
  loading: "idle" | "pending";
  error: undefined | unknown;
  number: number;
};

const initialState: TodoState = {
  loading: "idle",
  error: undefined,
  number: 0,
};

export const todoReducer = createSlice({
  name: "todoList",
  initialState,
  reducers: {
    minusCount: (state) => {
      state.number = state.number - 1;
    },
    plusCount: (state) => {
      state.number = state.number + 1;
    },
  },
});

export default todoReducer.reducer;
